import React, { useState, useRef, useEffect } from 'react';
import ReactPlayer from 'react-player';
import screenfull from 'screenfull';
import './VideoPlayer.css';

const VideoPlayer = () => {
  const [playing, setPlaying] = useState(false);
  const [volume, setVolume] = useState(0.5);
  const [boostVolume, setBoostVolume] = useState(1);
  const [aspectRatio, setAspectRatio] = useState('16:9');
  const [fullScreen, setFullScreen] = useState(false);
  const [fileUrl, setFileUrl] = useState('');
  const [controlVisible, setControlVisible] = useState(true);
  const [playbackRate, setPlaybackRate] = useState(1);
  const [progress, setProgress] = useState(0);
  const [subtitleTracks, setSubtitleTracks] = useState([]);
  const [selectedSubtitle, setSelectedSubtitle] = useState(null);

  const playerRef = useRef(null);
  const containerRef = useRef(null);

  const audioContextRef = useRef(null);
  const gainNodeRef = useRef(null);
  const sourceRef = useRef(null);
  const controlTimeout = useRef(null);

  useEffect(() => {
    // Initialize Web Audio API
    audioContextRef.current = new (window.AudioContext || window.webkitAudioContext)();
    gainNodeRef.current = audioContextRef.current.createGain();
    gainNodeRef.current.gain.value = 1; // Default to 1x volume

    // Auto-hide controls after 5 seconds of inactivity
    const resetControlTimeout = () => {
      if (controlTimeout.current) clearTimeout(controlTimeout.current);
      setControlVisible(true);
      controlTimeout.current = setTimeout(() => setControlVisible(false), 5000);
    };

    document.addEventListener('mousemove', resetControlTimeout);
    document.addEventListener('keydown', resetControlTimeout);

    return () => {
      document.removeEventListener('mousemove', resetControlTimeout);
      document.removeEventListener('keydown', resetControlTimeout);
    };
  }, []);

  const connectAudioNode = () => {
    if (!sourceRef.current && playerRef.current && playerRef.current.getInternalPlayer()) {
      const audioSource = audioContextRef.current.createMediaElementSource(playerRef.current.getInternalPlayer());
      sourceRef.current = audioSource;
      audioSource.connect(gainNodeRef.current).connect(audioContextRef.current.destination);
    }
  };

  // Function to toggle play/pause
  const handlePlayPause = () => {
    setPlaying(!playing);
  };

  // Function to handle volume change
  const handleVolumeChange = (e) => {
    setVolume(parseFloat(e.target.value));
    gainNodeRef.current.gain.value = parseFloat(e.target.value) * boostVolume;
  };

  // Function to boost volume
  const handleBoostVolume = () => {
    const newBoost = boostVolume >= 2 ? 1 : boostVolume + 0.5;
    setBoostVolume(newBoost);
    gainNodeRef.current.gain.value = volume * newBoost;
  };

  // Function to handle aspect ratio
  const handleAspectRatio = (ratio) => {
    setAspectRatio(ratio);
  };

  // Function to handle full screen
  const handleFullScreen = () => {
    if (screenfull.isEnabled) {
      screenfull.toggle(containerRef.current);
    }
    setFullScreen(!fullScreen);
  };

  // Function to handle file selection
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFileUrl(URL.createObjectURL(file));
      extractSubtitles(file);
    }
  };

  // Function to handle progress changes
  const handleProgressChange = (played) => {
    setProgress(played.played * 100);
  };

  // Function to handle seek
  const handleSeek = (e) => {
    const seekTo = (e.target.value / 100) * playerRef.current.getDuration();
    playerRef.current.seekTo(seekTo, 'seconds');
    setProgress(e.target.value);
  };

  // Function to handle playback speed
  const handlePlaybackRate = (rate) => {
    setPlaybackRate(rate);
    playerRef.current.getInternalPlayer().playbackRate = rate;
  };

  // Extract subtitle tracks from video
  const extractSubtitles = (file) => {
    const video = document.createElement('video');
    video.src = URL.createObjectURL(file);

    video.onloadedmetadata = () => {
      const tracks = [];
      for (let i = 0; i < video.textTracks.length; i++) {
        tracks.push({
          label: video.textTracks[i].label || `Subtitle ${i + 1}`,
          index: i
        });
      }
      setSubtitleTracks(tracks);
      setSelectedSubtitle(tracks[0].index); // Default to the first subtitle
      video.textTracks[0].mode = 'showing'; // Show first subtitle
    };
  };

  // Change subtitle track
  const handleSubtitleChange = (e) => {
    const selectedTrackIndex = parseInt(e.target.value, 10);
    setSelectedSubtitle(selectedTrackIndex);

    // Show selected subtitle and hide others
    const video = playerRef.current.getInternalPlayer();
    for (let i = 0; i < video.textTracks.length; i++) {
      video.textTracks[i].mode = i === selectedTrackIndex ? 'showing' : 'disabled';
    }
  };

  return (
    <div ref={containerRef} className="video-player-container" onMouseMove={() => setControlVisible(true)}>
      {/* File Picker */}
      <div className="file-picker">
        <input type="file" accept="video/*" onChange={handleFileChange} />
      </div>

      {/* Video Player */}
      {fileUrl && (
        <ReactPlayer
          ref={playerRef}
          url={fileUrl}
          playing={playing}
          volume={volume}
          playbackRate={playbackRate}
          onPlay={connectAudioNode} // Connect the audio node when playback starts
          onProgress={handleProgressChange}
          width="100%"
          height={aspectRatio === '16:9' ? '56.25%' : aspectRatio === '4:3' ? '75%' : '100%'}
          controls={false}
        />
      )}

      {/* Controls */}
      {controlVisible && (
        <div className="controls">
          <button className="control-button" onClick={handlePlayPause}>
            {playing ? 'Pause' : 'Play'}
          </button>

          <button className="control-button" onClick={() => handleAspectRatio('16:9')}>16:9</button>
          <button className="control-button" onClick={() => handleAspectRatio('4:3')}>4:3</button>
          <button className="control-button" onClick={() => handleAspectRatio('1:1')}>1:1</button>

          <div className="volume-container">
            <input
              className="volume-slider"
              type="range"
              min="0"
              max="1"
              step="0.01"
              value={volume}
              onChange={handleVolumeChange}
            />
            <button className="control-button boost-button" onClick={handleBoostVolume}>Boost Volume ({boostVolume}x)</button>
          </div>

          <div className="playback-controls">
            <button className="control-button" onClick={() => handlePlaybackRate(1)}>1x</button>
            <button className="control-button" onClick={() => handlePlaybackRate(1.5)}>1.5x</button>
            <button className="control-button" onClick={() => handlePlaybackRate(2)}>2x</button>
          </div>

          <input
            className="progress-bar"
            type="range"
            min="0"
            max="100"
            step="0.1"
            value={progress}
            onChange={handleSeek}
          />

          {/* Subtitle Selection */}
          {subtitleTracks.length > 0 && (
            <select value={selectedSubtitle} onChange={handleSubtitleChange} className="control-select">
              {subtitleTracks.map((track) => (
                <option key={track.index} value={track.index}>
                  {track.label}
                </option>
              ))}
            </select>
          )}

          <button className="control-button" onClick={handleFullScreen}>
            {fullScreen ? 'Exit Fullscreen' : 'Fullscreen'}
          </button>
        </div>
      )}
    </div>
  );
};

export default VideoPlayer;
