import React, { useState, useRef, useEffect } from 'react';
import ReactPlayer from 'react-player';
import screenfull from 'screenfull';
import './App.css';

// File handling and sorting
// import { openFileDialog, openFolderDialog } from './fileUtils'; // Utility functions to handle file & folder opening
// import { getSortedVideoFiles } from './fileSorter'; // Utility function for sorting files

export const getSortedVideoFiles = (folder) => {
  return Array.from(folder).filter(file => file.type.startsWith('video')).sort((a, b) => a.name.localeCompare(b.name));
};


export const openFileDialog = () => {
  return new Promise((resolve) => {
    const input = document.createElement('input');
    input.type = 'file';
    input.accept = 'video/*';
    input.onchange = (e) => {
      resolve(e.target.files[0]);
    };
    input.click();
  });
};

export const openFolderDialog = () => {
  return new Promise((resolve) => {
    const input = document.createElement('input');
    input.type = 'file';
    input.webkitdirectory = true;
    input.onchange = (e) => {
      resolve(e.target.files);
    };
    input.click();
  });
};


const App = () => {
  const [playing, setPlaying] = useState(false);
  const [volume, setVolume] = useState(0.5);
  const [boostVolume, setBoostVolume] = useState(1);
  const [aspectRatio, setAspectRatio] = useState('16:9');
  const [fullScreen, setFullScreen] = useState(false);
  const [fileUrl, setFileUrl] = useState('');
  const [controlVisible, setControlVisible] = useState(true);
  const [playbackRate, setPlaybackRate] = useState(1);
  const [progress, setProgress] = useState(0);
  const [subtitleTracks, setSubtitleTracks] = useState([]);
  const [selectedSubtitle, setSelectedSubtitle] = useState(null);
  const [theme, setTheme] = useState({ color: '#00ff00', background: '#000' }); // Button theme
  const [selectedFolder, setSelectedFolder] = useState(null);
  const [fileListPopupVisible, setFileListPopupVisible] = useState(false); // Folder file listing
  const [videoFiles, setVideoFiles] = useState([]); // Video files inside selected folder
  const [settingsVisible, setSettingsVisible] = useState(false); // Settings popup visibility

  const playerRef = useRef(null);
  const containerRef = useRef(null);
  const controlTimeout = useRef(null);
  const sidebarRef = useRef(null); // Sidebar ref for auto-hide

  useEffect(() => {
    // Auto-hide controls after 5 seconds of inactivity
    const resetControlTimeout = () => {
      if (controlTimeout.current) clearTimeout(controlTimeout.current);
      setControlVisible(true);
      controlTimeout.current = setTimeout(() => setControlVisible(false), 5000);
    };

    document.addEventListener('mousemove', resetControlTimeout);
    document.addEventListener('keydown', resetControlTimeout);

    return () => {
      document.removeEventListener('mousemove', resetControlTimeout);
      document.removeEventListener('keydown', resetControlTimeout);
    };
  }, []);

  // Handle sidebar auto-hide after 5 seconds
  const handleSidebarActivity = () => {
    if (controlTimeout.current) clearTimeout(controlTimeout.current);
    sidebarRef.current.style.visibility = 'visible';
    controlTimeout.current = setTimeout(() => {
      sidebarRef.current.style.visibility = 'hidden';
    }, 5000);
  };

  const handlePlayPause = () => setPlaying(!playing);

  const handleFileChange = async () => {
    const file = await openFileDialog(); // Open file dialog utility
    if (file) {
      const url = URL.createObjectURL(file);
      setFileUrl(url);
      setPlaying(true); // Start playing immediately
    }
  };

  const handleFolderChange = async () => {
    const folder = await openFolderDialog(); // Open folder dialog utility
    if (folder) {
      const sortedFiles = getSortedVideoFiles(folder); // Collect and sort video files
      setVideoFiles(sortedFiles);
      setSelectedFolder(folder);
      setFileListPopupVisible(true); // Show file list popup
    }
  };

  const handleProgressChange = (played) => setProgress(played.played * 100);

  const handleSeek = (e) => {
    const seekTo = (e.target.value / 100) * playerRef.current.getDuration();
    playerRef.current.seekTo(seekTo, 'seconds');
    setProgress(e.target.value);
  };

  const handlePlaybackRate = (rate) => {
    setPlaybackRate(rate);
    playerRef.current.getInternalPlayer().playbackRate = rate;
  };

  const handleFileClick = (file) => {
    const url = URL.createObjectURL(file);
    setFileUrl(url);
    setPlaying(true);
    setFileListPopupVisible(false); // Hide file list after selection
  };

  const handleSettingsChange = (newTheme) => setTheme(newTheme); // Update button colors and theme

  const handleOpenSettings = () => setSettingsVisible(true);
  const handleCloseSettings = () => setSettingsVisible(false);

  return (
    <div ref={containerRef} className="video-player-container" onMouseMove={handleSidebarActivity}>
      {/* Hamburger Menu */}
      <div ref={sidebarRef} className="hamburger-menu">
        <div className="hamburger-icon" onClick={handleSidebarActivity}>☰</div>
        <div className="sidebar">
          <button onClick={handleFileChange}>Open File</button>
          <button onClick={handleFolderChange}>Open Folder</button>
          {selectedFolder && <div>Selected Folder: {selectedFolder.name}</div>}
          <button onClick={handleOpenSettings}>Settings</button>
        </div>
      </div>

      {/* Video Player */}
      {fileUrl && (
        <ReactPlayer
          ref={playerRef}
          url={fileUrl}
          playing={playing}
          volume={volume}
          playbackRate={playbackRate}
          onProgress={handleProgressChange}
          width="100%"
          height={aspectRatio === '16:9' ? '56.25%' : aspectRatio === '4:3' ? '75%' : '100%'}
          controls={false}
        />
      )}

      {/* File List Popup */}
      {fileListPopupVisible && (
        <div className="file-list-popup">
          <table>
            <thead>
              <tr>
                <th>File Name</th>
                <th>Type</th>
                <th>Size</th>
                <th>Date Modified</th>
              </tr>
            </thead>
            <tbody>
              {videoFiles.map((file, index) => (
                <tr key={index} onDoubleClick={() => handleFileClick(file)}>
                  <td>{file.name}</td>
                  <td>{file.type}</td>
                  <td>{(file.size / (1024 * 1024)).toFixed(2)} MB</td>
                  <td>{new Date(file.lastModified).toLocaleDateString()}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}

      {/* Settings Popup */}
      {settingsVisible && (
        <div className="settings-popup">
          <h2>Settings</h2>
          <label>
            Button Color:
            <input
              type="color"
              value={theme.color}
              onChange={(e) => handleSettingsChange({ ...theme, color: e.target.value })}
            />
          </label>
          <label>
            Background Color:
            <input
              type="color"
              value={theme.background}
              onChange={(e) => handleSettingsChange({ ...theme, background: e.target.value })}
            />
          </label>
          <button onClick={handleCloseSettings}>Close</button>
        </div>
      )}
    </div>
  );
};

export default App;
