import React, { useState, useRef } from 'react';
import ReactPlayer from 'react-player';
import './App.css';

const App = () => {
  const [playing, setPlaying] = useState(false);
  const [volume, setVolume] = useState(0.5);
  const [boostVolume, setBoostVolume] = useState(1);
  const [aspectRatio, setAspectRatio] = useState('16:9');
  const [fullScreen, setFullScreen] = useState(false);
  const [fileUrl, setFileUrl] = useState('');
  const playerRef = useRef(null);

  // Function to toggle play/pause
  const handlePlayPause = () => {
    setPlaying(!playing);
  };

  // Function to handle volume change
  const handleVolumeChange = (e) => {
    setVolume(parseFloat(e.target.value));
  };

  // Function to boost volume
  const handleBoostVolume = () => {
    setBoostVolume(boostVolume >= 2 ? 1 : boostVolume + 0.5);
  };

  // Function to handle aspect ratio
  const handleAspectRatio = (ratio) => {
    setAspectRatio(ratio);
  };

  // Function to handle full screen
  const handleFullScreen = () => {
    setFullScreen(!fullScreen);
  };

  // Function to handle file selection
  const handleFileChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setFileUrl(URL.createObjectURL(file));
    }
  };

  return (
    <div className={`video-player-container ${fullScreen ? 'full-screen' : ''}`}>
      {/* File Picker */}
      <div className="file-picker">
        <input type="file" accept="video/*" onChange={handleFileChange} />
      </div>

      {/* Video Player */}
      {fileUrl && (
        <ReactPlayer
          ref={playerRef}
          url={fileUrl}
          playing={playing}
          volume={volume * boostVolume}
          width="100%"
          height={aspectRatio === '16:9' ? '56.25%' : aspectRatio === '4:3' ? '75%' : '100%'}
          controls={false}
        />
      )}

      {/* Controls */}
      <div className="controls">
        <button onClick={handlePlayPause}>
          {playing ? 'Pause' : 'Play'}
        </button>

        <button onClick={() => handleAspectRatio('16:9')}>16:9</button>
        <button onClick={() => handleAspectRatio('4:3')}>4:3</button>
        <button onClick={() => handleAspectRatio('1:1')}>1:1</button>

        <input
          type="range"
          min="0"
          max="1"
          step="0.01"
          value={volume}
          onChange={handleVolumeChange}
        />
        <button onClick={handleBoostVolume}>Boost Volume ({boostVolume}x)</button>

        <button onClick={handleFullScreen}>
          {fullScreen ? 'Exit Fullscreen' : 'Fullscreen'}
        </button>
      </div>
    </div>
  );
};

export default App;
